<?php     if(isset($_GET['action'])){        $action     =	$_GET['action'];    }        if(isset($_GET['id'])){        $id     =   $_GET['id'];	    }    global $wpdb;    $table  =	$wpdb->prefix.'paypal_products';    if($action=='update'){        if($_POST['updateprod']){            $product_name       =	$_POST['product_name'];            $return_url		=	$_POST['return_url'];            $responder		=	$_POST['responder'];            $product_price	=	$_POST['product_price'];            $shortcode      =	str_replace(" ","-", $product_name);            $shortcode      =	'[wp-paypal-product prodname="'.$shortcode.'"]';             $data   =   array(                            'product_name'      =>  $product_name,                            'return_url' 	=>  $return_url,                            'responder' 	=>  $responder,                            'product_price' 	=>  $product_price,                            'shortcode' 	=>  $shortcode,                        );                                        if(!empty($product_name) && !empty($product_price) && !empty($responder) && !empty($return_url)){                $ID         =	array('id'	=>	$id);                $update     =	$wpdb->update($table,$data,$ID);                $prod       =	"Product Updated Successfullly";            }else{                $eprod      =	"All fields are required";	            }        }    }elseif($action=='delete'){        $delete     =	$wpdb->query("DELETE FROM $table WHERE id='$id'");    }else{        if($_POST['addprod']){            $product_name	=	$_POST['product_name'];            $product_price	=	$_POST['product_price'];            $return_url		=	$_POST['return_url'];            $responder		=	$_POST['responder'];            $shortcode          =	str_replace(" ","-", $product_name);            $shortcode          =	'[wp-paypal-product prodname="'.$shortcode.'"]';             $data   =	array(                            'product_name' 	=>  $product_name,                            'return_url' 	=>  $return_url,                            'responder' 	=>  $responder,                            'product_price' 	=>  $product_price,                            'shortcode' 	=>  $shortcode,                        );            if(!empty($product_name) && !empty($product_price) && !empty($responder) && !empty($return_url)){                $insert     =	$wpdb->insert($table,$data) or die(mysql_error());                $prod       =	"Product Added Successfullly";            }else{                $eprod      =	"All fields are required";            }        }    }        $singleprod     = 	$wpdb->get_row("SELECT * FROM $table WHERE id ='$id'");     $table_resp     =	$wpdb->prefix.'paypal_responders';    $resp_link      =	"SELECT * FROM $table_resp";    $resp_res       = 	$wpdb->get_results( $resp_link );?>